				  $today=strtotime(date('Y-m-d H:i:s'));
				  $checkout=strtotime($row['checkout_time_info']);
				  $row['checkout_time_diff']=round(($today-$checkout)/(60)).' min ago';
				array_push($org,$row);
			}
			$stmt->close();
			$dbCon->close();
			 
			return $org;
		}
		
