					/* bind parameters for markers */
					$stmt->bind_param("si", $row[$openIndex],$closeId);	
					}
				}
				 
				$stmt->execute();
				$result = $stmt->get_result();
				$org=array();
				$i=0;
				while($rowTime = $result->fetch_assoc())
				{
					if($i==0)
