			/* bind parameters for markers */
			$stmt->bind_param("s", $data['certi']);
			$stmt->execute();
			$stmt->close();
			$dbCon->close();
			return 2;	
			}
			else
			{
			if($row['login_type']==1 || $row['login_type']==9)
			{	 
			$stmt = $dbCon->prepare("update user_certificates set login_status=2 where certificate_key=?");
			/* bind parameters for markers */
			$stmt->bind_param("s", $data['certi']);
			$stmt->execute();
							
			$stmt->close();
			$dbCon->close();
			return 1;
			}
			else if($row['login_type']==4)
			{	 
			$stmt = $dbCon->prepare("update user_certificates set login_status=2 where certificate_key=?");
			/* bind parameters for markers */
			$stmt->bind_param("s", $data['certi']);
			$stmt->execute();
							
			$stmt->close();
			$dbCon->close();
			return 1;
			}
