		function record_sort($records, $field, $reverse=false)
			{
				$hash = array();
			   
				foreach($records as $record)
				{
					$hash[$record[$field]] = $record;
				}
			   
				($reverse)? krsort($hash) : ksort($hash);
			   
				$records = array();
			   
				foreach($hash as $record)
				{
					$records []= $record;
				}
			   
				return $records;
			}

		
